mapper.select:
select apply.* , user.name as userName from `apply` left join user on apply.user_id = user.id
title like concat('%', #{title}, '%') and user_id =#{userId}
order by id desc

mapper.update:
update `apply` set title = #{title},content = #{content},
time = #{time},user_id = #{userId},reason = #{reason},status = #{status}
where id = #{id}
