mapper.select:
select introduction.*, category.title as categoryTitle, user.name as userName, user.avatar as userAvatar from `introduction` left join category on introduction.category_id = category.id left join user on introduction.user_id = user.id
and introduction.title like concat('%', #{title}, '%') and introduction.user_Id = #{userId}
order by id desc

mapper.update:
update `introduction` set img = #{img},title = #{title},content = #{content},
time = #{time},category_id=#{categoryId},user_id=#{userId}
where id = #{id}
